import React, { useState } from 'react';
import {
  TextField,
  Box,
  Autocomplete,
  CircularProgress,
  Paper,
} from '@mui/material';
import { motion } from 'framer-motion';
import axios from 'axios';
import { Movie } from '../types';

interface MovieSearchProps {
  onMovieSelect: (movie: Movie) => void;
}

const MovieSearch: React.FC<MovieSearchProps> = ({ onMovieSelect }) => {
  const [open, setOpen] = useState(false);
  const [options, setOptions] = useState<Movie[]>([]);
  const [loading, setLoading] = useState(false);
  const [inputValue, setInputValue] = useState('');

  const searchMovies = async (query: string) => {
    if (query.length < 2) return;

    setLoading(true);
    try {
      const response = await axios.get<Movie[]>(`/api/search?query=${query}`);
      setOptions(response.data);
    } catch (error) {
      console.error('Error searching movies:', error);
      setOptions([]);
    } finally {
      setLoading(false);
    }
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <Box sx={{ width: '100%', maxWidth: 600, mx: 'auto', mb: 4 }}>
        <Autocomplete
          open={open}
          onOpen={() => setOpen(true)}
          onClose={() => setOpen(false)}
          inputValue={inputValue}
          onInputChange={(_, newInputValue) => {
            setInputValue(newInputValue);
            searchMovies(newInputValue);
          }}
          options={options}
          loading={loading}
          getOptionLabel={(option) => option.title}
          onChange={(_, movie) => movie && onMovieSelect(movie)}
          renderInput={(params) => (
            <TextField
              {...params}
              label="Search for a movie"
              variant="outlined"
              fullWidth
              InputProps={{
                ...params.InputProps,
                endAdornment: (
                  <>
                    {loading ? (
                      <CircularProgress color="primary" size={20} />
                    ) : null}
                    {params.InputProps.endAdornment}
                  </>
                ),
              }}
            />
          )}
          PaperComponent={({ children }) => (
            <Paper
              sx={{
                background: 'rgba(30, 30, 30, 0.9)',
                backdropFilter: 'blur(10px)',
              }}
            >
              {children}
            </Paper>
          )}
        />
      </Box>
    </motion.div>
  );
};

export default MovieSearch;
